		$(".loader").show();
		$(".progress").css({
			"width": "10%"
		}).show();

		$.ajax({
			url: url,
			success: function(content) {

				if (content.redirect) {

					window.App.Router.navigate(content.redirect, {
						trigger: true
					});

					return;

				}

				$(".progress").animate({
					"width": "100%"
				}, "slow", function() {
					$(".progress").hide();
					$(".loader").hide();
				});


				content = '<div id="body-mock">' + content.replace(/^[\s\S]*<body.*?>|<\/body>[\s\S]*$/g, '') + '</div>';

				var dom = $(content);

				var result = {
					content: $("#content", dom).html(),
					header: $("header", dom).html(),
					nav: $("#nav", dom).html(),
					modal: $(".modal", dom).html(),
					classes: $("#classes", dom).attr("class")
				};

				//kill any modals
				$(".modal").remove();
				$("body").removeClass("md-show no-sidebar editor");
				$("body").addClass(result.classes);
				$("nav").removeClass("narrow");

				if (result.modal) {

					//$("body").addClass("modal-container");
					$("body").removeClass("no-sidebar");

					if ($(".overlay").length === 0) {
						//draw the overlay
						$("body").append("<div class='overlay'></div>");
					}

					var modal = $("<div>").addClass("modal md-content").append(result.modal);
					$("body").append(modal);


				} else {

					//kill overlays
					$(".overlay").remove();
					//kill modal styling
					$("body").removeClass("modal-container");
					//$("body").removeClass("no-sidebar");

					if (result.header) {
						$("header").html(result.header);
					}
					if (result.nav) {
						$("#nav").html(result.nav);
					}
					if (result.content) {
						$("#content").html(result.content);
					}

				}

				$("select").select();

				require(['jquery-form'], function() {
					$('form').on('submit', function(e) {
						e.preventDefault();
						$(this).ajaxSubmit({
							success: function(e) {
								try {
									if (e.redirect) {
										window.App.Router.navigate(e.redirect, {
											trigger: true
										});
									}
								} catch (ex) {
									console.log(e);
								}
							}
						});
					});
				});


				if (result.modal) {
					var reveal = function() {
						$("body").addClass("md-show");
						if (success) {
							success();
						}
					};
					setTimeout(reveal, 1000);
				} else {
					if (success) {
						success();
					}
				}

			}
		});

