/* jshint scripturl: false */
/*global define, document */

define('editor',['jquery', 'ace/ace'], function($, ace) {
	

	var isAttached = false;
	var sessions = [];
	var editor = null;

	//TABS

	$("#tabs").on("click", "a", function() {

		$(document).trigger("editor.change", {
			tab: $(this).parent(),
			session: sessions[$(this).parent().attr("data-filename")]
		});

	});

	//EDITOR 

	$(document).bind('keydown', 'meta+s', function(e) {

		if (e.keyCode === 83 && e.metaKey === true && editor != null) {

			var code = editor.getSession().getValue();
			var filename = $(".file.active").attr("data-filename");
			var data = new FormData();
			data.append('saved_file', new Blob(code),filename);
			$.ajax({
				cache: false,
    			contentType: false,
    			processData: false,
				type:'POST',
				url: "/" + $("input[name=application]").val() + "/application/file",
				data: data,
				success: function() {
					alert("SAVED FILE");
				}
			});

			e.preventDefault();

			return false;

		}

	});


	$(document).on("editor.change", function(event, file) {

		$(".file").removeClass("active");
		file.tab.addClass("active");
		
		editor.setSession(file.session);	
		

	});

	$(document).on("editor.open", function(event, file) {
		if (sessions[file.src]) {
			//it's open, trigger editor.change
			$(document).trigger("editor.change", {
				tab: $(".file[data-filename='" + file.src + "']"),
				session: sessions[file.src]
			});
			return;
		}

		$(".file").removeClass("active");
		//var position = $("#tabs .file").length;
		$("#tabs").append('<span class="file active" data-filename="' + file.src + '"><a href="javascript:">' + file.name + '</a></span>');

		//get binary data
		$.ajax({
			url: "/" + $("input[name=application]").val() + "/application/file/?src=" + encodeURIComponent(file.src),
			success: function(e, status, response) {

				var contentType = response.getResponseHeader("Content-Type").split(";")[0];

				var sessionMode = "ace/mode/";

				console.log(contentType);

				if (contentType === "text/html") {
					sessionMode += "html";
				}
				if (contentType === "application/javascript") {
					sessionMode += "javascript";
				}
				if (contentType === "text/css") {
					sessionMode += "css";
				}
				if (contentType === "text/x-markdown") {
					sessionMode += "markdown";
				}
				if(file.src.indexOf(".scss") !== -1) {
					sessionMode += "sass";
				}

				var session = ace.createEditSession(e, sessionMode);

				sessions[file.src] = session;

				$(document).trigger("editor.edit", session);

			}
		});

	});

	$(document).on("editor.edit", function(event, session) {

		if (!isAttached) {
			editor = ace.edit("editor");
			editor.setTheme("ace/theme/chrome");
			isAttached = true;
		}

		editor.setSession(session);

	});


	//FILE BROWSER

	var start = function() {

		isAttached = false;
		sessions = [];

		var fileList = $("#files");
		fileList.empty();
		$.ajax({
			url: $("input[name='files_src']").val(),
			success: function(e) {
				if (e.files && e.files.length > 0) {
					fileList.append("<ul>");
					fileList.append(draw(e.files));

					$(".folder > a").on("click", function() {
						$("> ul", $(this).parent()).toggle();
					});
					$(".file > a").on("click", function() {
						$(document).trigger("editor.open", {
							name: $(this).html(),
							src: $(this).data("filename")
						});
					});

				} else {
					fileList.append("No files found");
				}
			},
			error: function() {
				fileList.append("No files found");
			}
		});
	};

	var draw = function(files, el, folder, name) {
		var ul = $("<ul>");

		if (!name) {
			name = "";
		}

		var filename = name += (folder != null ? folder.name + "/" : "");

		$.each(files, function(i, obj) {

			var li = $("<li class='" + (obj.type === "folder" ? "folder" : "file") + "'>").append("<a href='javascript:' data-filename='/" + (filename + obj.name) + "'>" + obj.name + "</a>");
			if (obj.files) {
				draw(obj.files, li, obj, filename);
			}
			ul.append(li);
		});
		if (el) {
			el.append(ul);
			return el;
		} else {
			return ul;
		}
	};

	return start;


});